import AccountCircleOutlinedIcon from "@mui/icons-material/AccountCircleOutlined";
import AppsOutlinedIcon from "@mui/icons-material/AppsOutlined";
import HomeOutlinedIcon from "@mui/icons-material/HomeOutlined";
import { IconButton } from "@mui/material";
import React, { useState } from "react";
import "tailwindcss/tailwind.css";

const Footer = () => {
  const [where, setWhere] = useState({
    home: true,
    categories: false,
    profile: false,
  });
  return (
    <footer className=" fixed bottom-0 left-0 z-[100] w-full p-3 bg-my-background flex items-center justify-between md:gap-5 drop-shadow-header-shadow">
      <footerIcon Icon={HomeOutlinedIcon} iconText="Home" />
    </footer>
  );
};

export default Footer;

const FooterIcon = ({ Icon, iconText }) => {
  return (
    <div>
      <IconButton
        sx={{padding:"4px", borderRadius:"5px",color:"white"}}
      >
        <div className=" text-gray-700 hover:text-[#c82196] transition-all duration-300">
          <Icon />
          <p className=" text-xs font-semibold">{iconText}</p>
        </div>
      </IconButton>
    </div>
  );
};
