import React from "react";
import MainContainer from "../../Layout/MainContainer";
import { useSelector } from "react-redux";
import { categorySelector } from "../../Store/Slices/CategorySlice";
import { useParams } from "react-router-dom";

const categoryArr = [
  {
    imageURL: "https://www.bestoflanka.com/images/slider/best-things-to-do-in-sri-lanka/beach-destinations-sri-lanka/01.jpg",
    title: "Category name 1",
  },
  {
    imageURL: "https://www.bestoflanka.com/images/slider/best-things-to-do-in-sri-lanka/beach-destinations-sri-lanka/01.jpg",
    title: "Category name 2",
  },
  {
    imageURL: "https://www.bestoflanka.com/images/slider/best-things-to-do-in-sri-lanka/beach-destinations-sri-lanka/01.jpg",
    title: "Category name 3",
  },
  {
    imageURL: "https://www.bestoflanka.com/images/slider/best-things-to-do-in-sri-lanka/beach-destinations-sri-lanka/01.jpg",
    title: "Category name 4",
  },
]

const CategoryItems = () => {
  const category = useSelector(categorySelector);
  // console.log(category);

  const { categoryID } = useParams();
  const [categoryTitle] = category.filter((e)=>e.id === categoryID);


  return(
  <MainContainer>
    <h1 className=" text-lg font-bold mt-2 ml-2 mb-3">{categoryTitle.title}</h1>
    <div className="w-full grid grid-cols-3 grid-rows[auto] gap-16">

    </div>
    </MainContainer>
  );
};

export default CategoryItems;

const CategoryItemUnit = () => {
  return(
    <div className="w-full h-[200px] bg-red-500 rounded-lg">
      <img/>
      <h3></h3>
    </div>
  );
}
