import React from "react";
import MainContainer from "../../Layout/MainContainer";
import { useSelector } from "react-redux";
import { categorySelector } from "../../Store/Slices/CategorySlice";
import { useParams } from "react-router-dom";
import { Rating } from "@mui/material";

const categoryArr = [
  {
    imageURL: "https://www.bestoflanka.com/images/slider/best-things-to-do-in-sri-lanka/beach-destinations-sri-lanka/01.jpg",
    title: "Category name 1",
    val:2
  },
  {
    imageURL: "https://www.bestoflanka.com/images/slider/best-things-to-do-in-sri-lanka/beach-destinations-sri-lanka/01.jpg",
    title: "Category name 2",
    val:2.5
  },
  {
    imageURL: "https://www.bestoflanka.com/images/slider/best-things-to-do-in-sri-lanka/beach-destinations-sri-lanka/01.jpg",
    title: "Category name 3",
    val:4.5
  },
  {
    imageURL: "https://www.bestoflanka.com/images/slider/best-things-to-do-in-sri-lanka/beach-destinations-sri-lanka/01.jpg",
    title: "Category name 4",
    val:3
  },
]

const CategoryItems = () => {
  const category = useSelector(categorySelector);
  // console.log(category);

  const { categoryID } = useParams();
  const [categoryTitle] = category.filter((e)=>e.id === categoryID);


  return(
  <MainContainer>
    <h1 className=" text-lg font-bold mt-2 ml-2 mb-3">{categoryTitle.title}</h1>
    <div className="w-full grid grid-cols-3 grid-rows[auto] gap-">
      {categoryArr.map((e,i)=>(
        <CategoryItemUnit key={i} imageUrl={e.imageURL} title={e.title} val={e.val}/>
      ))}
    </div>
    </MainContainer>
  );
};

export default CategoryItems;

const CategoryItemUnit = ({imageUrl , title , val}) => {
  return(
    <div style={{color:"#26c6da", backgroundColor:"#e91e63"}} className="w-full object-contain rounded-lg">
      <img src={imageUrl} alt={title}/>
      <h3>{title}</h3>
      <Rating
        name={imageUrl}
        value={val}
        precision={0.1}
        size="small"
        readOnly
      />
    </div>
  );
}
