import React, { useRef, useState } from 'react';
import { Swiper, SwiperSlide } from "swiper/react";
import { Grid, Pagination } from "swiper/modules";

import 'swiper/css';
import 'swiper/css/grid';
import 'swiper/css/pagination';

const ProductArray = [
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/monmouth-beach-snow-march-2018.jpg",
    price: "100",
    productTile: "Monmouth Beach",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/snow-beachhuts-monmouth-march2018.jpg",
    price: "102",
    productTile: "Snow Beachhuts",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/snow-entrance-langmoor-march2018.jpg",
    price: "125",
    productTile: "Snow Entrance",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/snow-lymeregis-march2018-pebblebeach.jpg",
    price: "150",
    productTile: "Snow Lyme Regis",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/snow-march-2018-lymeregis.jpg",
    price: "200",
    productTile: "Snow March",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/langmoor-gardens-boardwalk-snow-march2018.jpg",
    price: "250",
    productTile: "Langmoor Gardens",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/monmouth-beach-snow-march-2018.jpg",
    price: "100",
    productTile: "Monmouth Beach",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/snow-beachhuts-monmouth-march2018.jpg",
    price: "102",
    productTile: "Snow Beachhuts",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/snow-entrance-langmoor-march2018.jpg",
    price: "125",
    productTile: "Snow Entrance",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/snow-lymeregis-march2018-pebblebeach.jpg",
    price: "150",
    productTile: "Snow Lyme Regis",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/snow-march-2018-lymeregis.jpg",
    price: "200",
    productTile: "Snow March",
  },
  {
    imageUrl:
      "https://lovelymeregis.co.uk/thumbs/Images/gallery/langmoor-gardens-boardwalk-snow-march2018.jpg",
    price: "250",
    productTile: "Langmoor Gardens",
  },
];

const Product = ({ title, rowsCount }) => {
  return (
    <section>
      <h1>{title}</h1>

      <Swiper
        slidesPerView={4}
        grid={{
          rows: Number(rowsCount),
        }}
        spaceBetween={30}
        pagination={{
          clickable: true,
        }}
        modules={[Grid, Pagination]}
        className="mySwiper"
      >
        {ProductArray.map(({ imageUrl, price, productTile }, index) => (
          <SwiperSlide key={index}>
            <ProductUnit
              imageUrl={imageUrl}
              price={price}
              productTile={productTile}
              id={index}
            />
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
};

export default Product;

const ProductUnit = ({ imageUrl, price, productTile, id }) => {
  <div className="w-full">
    <img
      src={imageUrl}
      alt={`product unit ${id}`}
      className="w-full object-contain "
    />
    <h3>{productTile}</h3>
    <p>Rs.{price}</p>
  </div>;
};
