import { useRef } from "react"
import MainContainer from "../../Layout/MainContainer"
import { IconButton } from "@mui/material"

const itemIImageArr = [
  "https://majesticjourney.in/wp-content/uploads/2022/05/goa-featured.jpg",
  "https://majesticjourney.in/wp-content/uploads/2022/05/goa-featured.jpg",
  "https://majesticjourney.in/wp-content/uploads/2022/05/goa-featured.jpg",
  "https://majesticjourney.in/wp-content/uploads/2022/05/goa-featured.jpg",
]

const ItemPage = () => {

  const mainImageRef = useRef()
  const subImageRef = useRef([])

  return (
    <MainContainer>
      <h1 className="text-lg font-bold mb-7">Item title</h1>
      <img
        src={itemIImageArr[0]}
        alt="item"
        className="w-full h-[400px] rounded-md object-cover"
        ref={mainImageRef}
      />
      <div className=" w-full mt-3 grid grid-cols-4 text-center grid-rows-1 gap-0">
        {itemIImageArr.map((e, i) => (
          <IconButton key={i}>
            <img
              src={e}
              alt="item"
              className="w-full"
              ref={(el) => (subImageRef.current[i] = el)}
              onMouseEnter={() => {
                mainImageRef.current.src = e;
              }}
            />
          </IconButton>
        ))}
      </div>
    </MainContainer>
  );
}

export default ItemPage
